import React, { useState } from 'react';
import '../styles/UploadForm.css'; // âœ… plain CSS import

const UploadForm = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => setFile(e.target.files[0]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) return alert('Please select an image.');

    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    try {
      const res = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        body: formData,
      });

      if (!res.ok) throw new Error('Server error');

      const data = await res.json();
      if (data.class && data.confidence) {
        setResult({
          className: data.class,
          confidence: data.confidence,
          description: data.description,
          care: data.care,
        });
      } else {
        setResult({ error: data.error || 'No prediction received' });
      }
    } catch (error) {
      console.error('Prediction error:', error);
      setResult({ error: 'Something went wrong.' });
    }
    setLoading(false);
  };

  return (
    <div className="upload-wrapper">
      <h1 className="heading">ðŸŒ¿ Plant Disease Detector</h1>
      <form onSubmit={handleSubmit} className="form-container">
        <h2 className="form-title">Upload a Plant Image</h2>
        <input type="file" accept="image/*" onChange={handleChange} className="file-input" />
        <button type="submit" className="submit-btn" disabled={loading}>
          {loading ? 'Predicting...' : 'Predict'}
        </button>
      </form>

      {result && (
        <div className="result-box">
          {result.error ? (
            <p className="error-text"><strong>Error:</strong> {result.error}</p>
          ) : (
            <>
              <p><strong>Predicted Disease:</strong> {result.className}</p>
              <p><strong>Confidence:</strong> {result.confidence}</p>
              <p><strong>Description:</strong> {result.description}</p>
              <p><strong>Care Tips:</strong> {result.care}</p>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default UploadForm;
