import React, { useState } from 'react';
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
import '../styles/UploadForm.css';

const UploadForm = () => {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setPreview(URL.createObjectURL(selectedFile));
    setResult(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) return alert('Please select an image.');

    const formData = new FormData();
    formData.append('file', file);
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        body: formData,
      });

      const data = await res.json();
      if (data.class && data.confidence) {
        setResult({
          className: data.class,
          confidence: data.confidence,
          description: data.description,
          care: data.care,
        });
      } else {
        setResult({ error: data.error || 'No prediction received' });
      }
    } catch (error) {
      console.error('Prediction error:', error);
      setResult({ error: 'Something went wrong.' });
    }
    setLoading(false);
  };

  const handleDownloadPDF = () => {
    if (!result) return;
    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text("üåø Plant Disease Detection Result", 14, 20);
    autoTable(doc, {
      startY: 30,
      head: [['Field', 'Value']],
      body: [
        ['Disease', result.className],
        ['Confidence', result.confidence],
        ['Description', result.description],
        ['Care Tips', result.care],
      ],
    });
    doc.save('plant_disease_result.pdf');
  };

  return (
    <>
      <nav className="navbar">
        <span className="logo">üåø Plant Disease Detection</span>
        <div className="nav-links">
          <a href="#home">Home</a>
          <a href="#about">About</a>
          <a href="#contact">Contact</a>
        </div>
      </nav>

      <div className="form-section" id="home">
        <form onSubmit={handleSubmit} className="form-box">
          <h2>Upload a Plant Image</h2>

          <input
            type="file"
            accept="image/*"
            onChange={handleChange}
            className="file-input"
          />

          {preview && (
            <div className="image-preview">
              <img src={preview} alt="Preview" />
            </div>
          )}

          <button type="submit" className="submit-btn" disabled={loading}>
            {loading ? 'Predicting...' : 'Predict'}
          </button>

          {result && (
            <div className="result-box">
              {result.error ? (
                <p className="error-text">‚ùå {result.error}</p>
              ) : (
                <>
                  <p><strong>ü©∫ Disease:</strong> {result.className}</p>
                  <p><strong>üìä Confidence:</strong> {result.confidence}</p>
                  <p><strong>‚ÑπÔ∏è Description:</strong> {result.description}</p>
                  <p><strong>üå± Care Tips:</strong> {result.care}</p>
                  <button onClick={handleDownloadPDF} type="button" className="download-btn">
                    üì• Download PDF
                  </button>
                </>
              )}
            </div>
          )}
        </form>
      </div>

      <section id="about" className="info-section">
        <h3>About</h3>
        <p>Plant Disease Detection App helps you find out if your plant is healthy or sick.
Just upload a leaf photo, and the app will tell you the disease, how confident it is, and how to take care of it.
It uses smart technology and learns from many plant images. Easy, fast, and helpful for all plant lovers</p>
      </section>

      <section id="contact" className="info-section">
        <h3>Contact</h3>
        <p>Email: support@plantai.com | GitHub: @yourrepo</p>
      </section>

      <footer className="footer">¬© {new Date().getFullYear()} Plant Disease Detection. All rights reserved.</footer>
    </>
  );
};

export default UploadForm;
