import React, { useState } from 'react';
import '../styles/UploadForm.css'; 
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';

const UploadForm = () => {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const selected = e.target.files[0];
    setFile(selected);
    setPreview(URL.createObjectURL(selected));
    setResult(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) return alert('Please select an image.');

    const formData = new FormData();
    formData.append('file', file);
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        body: formData,
      });

      const data = await res.json();
      if (data.class && data.confidence) {
        setResult({
          className: data.class,
          confidence: data.confidence,
          description: data.description,
          care: data.care,
        });
      } else {
        setResult({ error: data.error || 'No prediction received.' });
      }
    } catch (err) {
      setResult({ error: 'Something went wrong.' });
    }

    setLoading(false);
  };

  const handleDownloadPDF = () => {
    if (!result) return;
    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text("ðŸŒ¿ Plant Disease Detection Result", 14, 20);
    autoTable(doc, {
      startY: 30,
      head: [['Field', 'Value']],
      body: [
        ['Disease', result.className],
        ['Confidence', result.confidence],
        ['Description', result.description],
        ['Care Tips', result.care],
      ],
    });
    doc.save('plant_disease_result.pdf');
  };

  return (
    <>
      <nav className="navbar">
        <span className="brand">ðŸŒ¿ Plant Disease Detection</span>
        <div className="links">
          <a href="#">Home</a>
          <a href="#about">About</a>
          <a href="#contact">Contact</a>
        </div>
      </nav>

      <form onSubmit={handleSubmit} className="form-box">
        <h2>Upload a Plant Image</h2>
        <div className="image-preview">
          {preview && <img src={preview} alt="Preview" />}
        </div>
        <label className="file-btn">
          Choose File
          <input type="file" accept="image/*" onChange={handleChange} />
        </label>
        <p className="file-name">{file?.name}</p>
        <button type="submit" disabled={loading}>
          {loading ? 'Predicting...' : 'Predict'}
        </button>

        {result && (
          <div className="result-box">
            {result.error ? (
              <p className="error"><strong>Error:</strong> {result.error}</p>
            ) : (
              <>
                <p><strong>âœ… Predicted Disease:</strong> {result.className}</p>
                <p><strong>ðŸ“Š Confidence:</strong> {result.confidence}</p>
                <p>{result.description}</p>
                <p>ðŸ’¡ {result.care}</p>
                <button className="download" onClick={handleDownloadPDF}>ðŸ“¥ Download PDF</button>
              </>
            )}
          </div>
        )}
      </form>

      <footer className="footer">
        Â© {new Date().getFullYear()} Plant Disease Detection. All rights reserved.
      </footer>
    </>
  );
};

export default UploadForm;
